	<script>
// ===== COIN ANIMATION EFFECTS =====
document.addEventListener('DOMContentLoaded', function() {
    // Coin SVG data
    const coinSVG = `
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 512 512">
            <path fill="#FFD700" d="M256 0C114.6 0 0 114.6 0 256s114.6 256 256 256s256-114.6 256-256S397.4 0 256 0zM256 464c-114.7 0-208-93.31-208-208S141.3 48 256 48s208 93.31 208 208S370.7 464 256 464z"/>
            <path fill="#FFD700" d="M256 128c-70.69 0-128 57.31-128 128s57.31 128 128 128s128-57.31 128-128S326.7 128 256 128zM256 336c-44.11 0-80-35.89-80-80s35.89-80 80-80s80 35.89 80 80S300.1 336 256 336z"/>
            <path fill="#FFA500" d="M256 160c-53.02 0-96 42.98-96 96s42.98 96 96 96s96-42.98 96-96S309 160 256 160z"/>
            <path fill="#B8860B" d="M256 224c-17.67 0-32 14.33-32 32s14.33 32 32 32s32-14.33 32-32S273.7 224 256 224z"/>
        </svg>
    `;
    
    // Coin animation CSS
    const coinStyles = document.createElement('style');
    coinStyles.textContent = `
        @keyframes coinFloat {
            0% {
                transform: translateY(0) rotate(0deg);
                opacity: 1;
            }
            25% {
                transform: translateY(-20px) rotate(90deg);
            }
            50% {
                transform: translateY(-40px) rotate(180deg);
            }
            75% {
                transform: translateY(-20px) rotate(270deg);
            }
            100% {
                transform: translateY(0) rotate(360deg);
                opacity: 0;
            }
        }
        
        @keyframes coinFall {
            0% {
                transform: translateY(-100px) rotate(0deg);
                opacity: 1;
            }
            100% {
                transform: translateY(100vh) rotate(720deg);
                opacity: 0;
            }
        }
        
        @keyframes coinSpin {
            0% {
                transform: rotateY(0deg);
            }
            100% {
                transform: rotateY(360deg);
            }
        }
        
        @keyframes coinCollect {
            0% {
                transform: translate(var(--startX), var(--startY)) scale(1);
                opacity: 1;
            }
            100% {
                transform: translate(var(--endX), var(--endY)) scale(0.3);
                opacity: 0;
            }
        }
        
        @keyframes coinPulse {
            0%, 100% {
                transform: scale(1);
                filter: brightness(1);
            }
            50% {
                transform: scale(1.1);
                filter: brightness(1.3);
            }
        }
        
        .coin {
            position: fixed;
            width: 40px;
            height: 40px;
            pointer-events: none;
            z-index: 9999;
            cursor: pointer;
            transition: transform 0.3s ease;
        }
        
        .coin:hover {
            transform: scale(1.2);
        }
        
        .coin-collecting {
            animation: coinCollect 0.8s ease-out forwards !important;
        }
        
        .coin-counter {
            position: fixed;
            top: 20px;
            right: 20px;
            background: rgba(0, 0, 0, 0.8);
            color: #FFD700;
            padding: 10px 15px;
            border-radius: 20px;
            font-family: Arial, sans-serif;
            font-weight: bold;
            font-size: 18px;
            z-index: 10000;
            display: flex;
            align-items: center;
            gap: 10px;
            border: 2px solid #FFD700;
            box-shadow: 0 0 15px rgba(255, 215, 0, 0.5);
        }
        
        .coin-counter::before {
            content: '';
            width: 20px;
            height: 20px;
            background: #FFD700;
            border-radius: 50%;
            display: inline-block;
            animation: coinSpin 2s linear infinite;
        }
        
        .coin-bonus {
            position: fixed;
            background: linear-gradient(45deg, #FFD700, #FFA500);
            color: #000;
            padding: 5px 10px;
            border-radius: 10px;
            font-weight: bold;
            z-index: 10000;
            animation: coinFloat 1s ease-out forwards;
            pointer-events: none;
        }
    `;
    document.head.appendChild(coinStyles);
    
    // Coin counter
    let coinCount = 0;
    let totalCoinsCollected = 0;
    const coinCounter = document.createElement('div');
    coinCounter.className = 'coin-counter';
    coinCounter.innerHTML = `<span>0</span> Coins`;
    document.body.appendChild(coinCounter);
    
    // Update coin counter
    function updateCoinCounter() {
        const counterSpan = coinCounter.querySelector('span');
        counterSpan.textContent = coinCount;
        
        // Pulse effect when coin count changes
        coinCounter.style.animation = 'coinPulse 0.3s ease';
        setTimeout(() => {
            coinCounter.style.animation = '';
        }, 300);
    }
    
    // Create random coins
    function createRandomCoin() {
        const coin = document.createElement('div');
        coin.className = 'coin';
        coin.innerHTML = coinSVG;
        
        // Random position
        const posX = Math.random() * (window.innerWidth - 40);
        const posY = Math.random() * (window.innerHeight - 40);
        
        // Random size
        const size = Math.random() * 20 + 20;
        
        // Random animation
        const animations = ['coinFloat', 'coinFall'];
        const randomAnim = animations[Math.floor(Math.random() * animations.length)];
        const animDuration = Math.random() * 3 + 2;
        
        coin.style.cssText = `
            left: ${posX}px;
            top: ${posY}px;
            width: ${size}px;
            height: ${size}px;
            animation: ${randomAnim} ${animDuration}s linear forwards;
            opacity: ${Math.random() * 0.3 + 0.7};
        `;
        
        // Click to collect
        coin.addEventListener('click', function(e) {
            e.stopPropagation();
            collectCoin(this, posX + size/2, posY + size/2);
        });
        
        document.body.appendChild(coin);
        
        // Remove after animation
        setTimeout(() => {
            if (coin.parentNode) {
                coin.remove();
            }
        }, animDuration * 1000);
    }
    
    // Create multiple random coins
    function createRandomCoins(count) {
        for (let i = 0; i < count; i++) {
            setTimeout(() => {
                createRandomCoin();
            }, i * 300);
        }
    }
    
    // Collect coin animation
    function collectCoin(coin, coinX, coinY) {
        if (coin.classList.contains('coin-collecting')) return;
        
        coin.classList.add('coin-collecting');
        
        // Get coin counter position
        const counterRect = coinCounter.getBoundingClientRect();
        const counterX = counterRect.left + counterRect.width / 2;
        const counterY = counterRect.top + counterRect.height / 2;
        
        // Set animation parameters
        coin.style.setProperty('--startX', '0px');
        coin.style.setProperty('--startY', '0px');
        coin.style.setProperty('--endX', `${counterX - coinX}px`);
        coin.style.setProperty('--endY', `${counterY - coinY}px`);
        
        // Update coin count
        coinCount += Math.floor(Math.random() * 5) + 1;
        totalCoinsCollected++;
        updateCoinCounter();
        
        // Show bonus text
        showBonusText(coinX, coinY, '+1');
        
        // Remove coin after animation
        setTimeout(() => {
            if (coin.parentNode) {
                coin.remove();
            }
        }, 800);
        
        // Check for special events
        checkCoinEvents();
    }
    
    // Show bonus text
    function showBonusText(x, y, text) {
        const bonus = document.createElement('div');
        bonus.className = 'coin-bonus';
        bonus.textContent = text;
        bonus.style.left = `${x}px`;
        bonus.style.top = `${y}px`;
        
        document.body.appendChild(bonus);
        
        setTimeout(() => {
            if (bonus.parentNode) {
                bonus.remove();
            }
        }, 1000);
    }
    
    // Special events based on coins collected
    function checkCoinEvents() {
        // Every 10 coins
        if (totalCoinsCollected > 0 && totalCoinsCollected % 10 === 0) {
            createCoinRain();
            showBonusText(
                window.innerWidth / 2,
                window.innerHeight / 2,
                '🎉 BONUS: +10 COINS! 🎉'
            );
            coinCount += 10;
            updateCoinCounter();
        }
        
        // Every 25 coins
        if (totalCoinsCollected > 0 && totalCoinsCollected % 25 === 0) {
            createGoldenCoinStorm();
            showBonusText(
                window.innerWidth / 2,
                window.innerHeight / 2 - 50,
                '🔥 GOLDEN COIN STORM! 🔥'
            );
        }
        
        // Every 50 coins
        if (totalCoinsCollected > 0 && totalCoinsCollected % 50 === 0) {
            coinCount += 50;
            updateCoinCounter();
            showBonusText(
                window.innerWidth / 2,
                window.innerHeight / 2 - 100,
                '💎 JACKPOT: +50 COINS! 💎'
            );
        }
    }
    
    // Coin rain effect
    function createCoinRain() {
        const rainCount = 20;
        for (let i = 0; i < rainCount; i++) {
            setTimeout(() => {
                const coin = document.createElement('div');
                coin.className = 'coin';
                coin.innerHTML = coinSVG;
                
                const posX = Math.random() * (window.innerWidth - 40);
                const size = 30;
                
                coin.style.cssText = `
                    left: ${posX}px;
                    top: -50px;
                    width: ${size}px;
                    height: ${size}px;
                    animation: coinFall ${Math.random() * 1 + 1}s linear forwards;
                    opacity: 0.9;
                `;
                
                // Make rain coins collectable
                coin.addEventListener('click', function(e) {
                    e.stopPropagation();
                    const rect = this.getBoundingClientRect();
                    collectCoin(this, rect.left + size/2, rect.top + size/2);
                });
                
                document.body.appendChild(coin);
                
                setTimeout(() => {
                    if (coin.parentNode) {
                        coin.remove();
                    }
                }, 2000);
            }, i * 100);
        }
    }
    
    // Golden coin storm
    function createGoldenCoinStorm() {
        const stormCount = 50;
        for (let i = 0; i < stormCount; i++) {
            setTimeout(() => {
                const coin = document.createElement('div');
                coin.className = 'coin';
                coin.innerHTML = coinSVG;
                
                const posX = Math.random() * (window.innerWidth - 40);
                const posY = Math.random() * (window.innerHeight - 40);
                const size = Math.random() * 15 + 10;
                
                // Golden coin with glow
                coin.style.cssText = `
                    left: ${posX}px;
                    top: ${posY}px;
                    width: ${size}px;
                    height: ${size}px;
                    animation: coinFloat ${Math.random() * 2 + 1}s ease-in-out infinite alternate;
                    opacity: 1;
                    filter: drop-shadow(0 0 10px gold);
                `;
                
                // Make storm coins collectable
                coin.addEventListener('click', function(e) {
                    e.stopPropagation();
                    const rect = this.getBoundingClientRect();
                    collectCoin(this, rect.left + size/2, rect.top + size/2);
                });
                
                document.body.appendChild(coin);
                
                // Remove after 5 seconds
                setTimeout(() => {
                    if (coin.parentNode) {
                        coin.remove();
                    }
                }, 5000);
            }, i * 50);
        }
    }
    
    // Spawn coins on click anywhere
    document.addEventListener('click', function(e) {
        // 10% chance to spawn coin on click
        if (Math.random() < 0.1 && e.target.className !== 'coin') {
            const coin = document.createElement('div');
            coin.className = 'coin';
            coin.innerHTML = coinSVG;
            
            const size = 30;
            
            coin.style.cssText = `
                left: ${e.clientX - size/2}px;
                top: ${e.clientY - size/2}px;
                width: ${size}px;
                height: ${size}px;
                animation: coinFloat 3s ease-out forwards;
                opacity: 0.9;
            `;
            
            // Make spawn coins collectable
            coin.addEventListener('click', function(clickEvent) {
                clickEvent.stopPropagation();
                const rect = this.getBoundingClientRect();
                collectCoin(this, rect.left + size/2, rect.top + size/2);
            });
            
            document.body.appendChild(coin);
            
            setTimeout(() => {
                if (coin.parentNode) {
                    coin.remove();
                }
            }, 3000);
        }
    });
    
    // Spawn coins on scroll
    let lastScrollTime = 0;
    window.addEventListener('scroll', function() {
        const currentTime = Date.now();
        if (currentTime - lastScrollTime > 1000) { // 1 second cooldown
            // 20% chance to spawn coin on scroll
            if (Math.random() < 0.2) {
                createRandomCoin();
            }
            lastScrollTime = currentTime;
        }
    });
    
    // Auto-spawn coins periodically
    setInterval(() => {
        if (Math.random() < 0.3) { // 30% chance every 5 seconds
            createRandomCoin();
        }
    }, 5000);
    
    // Initialize with some coins
    setTimeout(() => {
        createRandomCoins(3);
    }, 1000);
    
    // Save coins to localStorage
    function saveCoins() {
        localStorage.setItem('b200m_coins', coinCount);
        localStorage.setItem('b200m_total_coins', totalCoinsCollected);
    }
    
    // Load coins from localStorage
    function loadCoins() {
        const savedCoins = localStorage.getItem('b200m_coins');
        const savedTotal = localStorage.getItem('b200m_total_coins');
        
        if (savedCoins) {
            coinCount = parseInt(savedCoins);
            totalCoinsCollected = parseInt(savedTotal) || 0;
            updateCoinCounter();
        }
    }
    
    // Auto-save every 10 seconds
    setInterval(saveCoins, 10000);
    
    // Save on page unload
    window.addEventListener('beforeunload', saveCoins);
    
    // Load saved coins
    loadCoins();
    
    // Reset button (for testing)
    const resetBtn = document.createElement('button');
    resetBtn.textContent = 'Reset Coins';
    resetBtn.style.cssText = `
        position: fixed;
        bottom: 20px;
        right: 20px;
        padding: 5px 10px;
        background: #ff4444;
        color: white;
        border: none;
        border-radius: 5px;
        cursor: pointer;
        z-index: 10000;
        font-size: 12px;
    `;
    resetBtn.addEventListener('click', function() {
        coinCount = 0;
        totalCoinsCollected = 0;
        updateCoinCounter();
        localStorage.removeItem('b200m_coins');
        localStorage.removeItem('b200m_total_coins');
        showBonusText(window.innerWidth/2, window.innerHeight/2, 'Coins Reset!');
    });
    document.body.appendChild(resetBtn);
});
</script>
	
